class bt{constructor(element){switch(element.charAt(0)){case '#':element.removeCharAt(0);this.el=document.getElementById(element);break;case '.':element.removeCharAt(0);this.el=document.getElementsByClassName(element);break;default:this.el=document.getElementsByTagName(element);break}};text(input){el.textContent=input};i_html(input){el.innerHTML=input};o_html(input){el.outterHTML=input};get(what){switch(what){case 'id':return el.id;break;case 'tag':return el.tagName;break;default:return;break}}};class ajax{constructor(uri,dat){this.uri=uri;this.dat=dat};get(){var out;const xhttp=new XMLHttpRequest();xhttp.onload=function(){out=this.responseText};xhttp.open("GET",this.uri,!0);xhttp.send();return out};post(){var out;const xhttp=new XMLHttpRequest();xhttp.onload=function(){out=this.responseText};xhttp.open("POST",this.uri);xhttp.setRequestHeader("Content-type",this.dat);xhttp.send();return out};forum(uri,data){var out;const xhttp=new XMLHttpRequest();xhttp.onload=function(){out=this.responseText};xhttp.open("POST",this.uri,!0);xhttp.setRequestHeader("Content-type","application/x-www-form-urlencoded");xhttp.send(data);return out}}